import { fetchList } from '@/api/article'
import { parseTime } from '@/utils'
// options components
import FilenameOption from './components/FilenameOption'
import AutoWidthOption from './components/AutoWidthOption'
import BookTypeOption from './components/BookTypeOption'
import axios from 'axios';
import gettoken from '../../../src/store/modules/user.js'
export default {
  name: 'ExportExcel',
  components: { FilenameOption, AutoWidthOption, BookTypeOption },
  data() {
    return {
      list: null,
      listLoading: true,
      downloadLoading: false,
      filename: '预约信息',
      autoWidth: true,
      bookType: 'xlsx',

    }
  },
  created() {
    this.fetchData()
  },
  methods: {
    fetchData() {
      this.listLoading = true
      var vm=this
      this.loading=true;
      const path = 'http://127.0.0.1:5000/api/appointlist';
            axios.get(path,{params:{token:gettoken.state.token}
               
              })
              .then((res) => {
                this.list = res.data;
                this.listLoading = false
              })
              .catch((error) => {
                // eslint-disable-next-line
                vm.$message({
                        type: 'info',
                        message: '出错啦！获取数据失败'
                });
                this.loading=false
              });
    },
    handleDownload() {
      this.downloadLoading = true
      import('@/vendor/Export2Excel').then(excel => {
        const tHeader = [ '书名', 'ISDN', '用户id', '日期']
        const filterVal = [ 'bookname', 'bookisdn', 'userid', 'opertime']
        const list = this.list
        const data = this.formatJson(filterVal, list)
        excel.export_json_to_excel({
          header: tHeader,
          data,
          filename: this.filename,
          autoWidth: this.autoWidth,
          bookType: this.bookType
        })
        this.downloadLoading = false
      })
    },
    formatJson(filterVal, jsonData) {
      return jsonData.map(v => filterVal.map(j => {
        if (j === 'timestamp') {
          return parseTime(v[j])
        } else {
          return v[j]
        }
      }))
    },
    handleDelete(row) {
      var vm=this
        this.$confirm('确认发放《'+row.bookname+'》，给'+row.userid+',确定吗', '提示', {
                  confirmButtonText: '确定',
                  cancelButtonText: '取消',
                  type: 'warning'
                }).then(() => {
                  axios.post('http://127.0.0.1:5000/api/confirmregist', {
                    
                     userid:row.userid,
                     bookisdn:row.bookisdn,

                    })

                    .then(function (response) {
                    //  this.$refs[temp].resetFields();
                      vm.$message({
                          type: 'success',
                          message: '操作成功!'
                        });
                    })
                    .catch(function (error) {
                      vm.$message({
                          type: 'info',
                          message: error
                        });
                  })

                  setTimeout(() => {
                    this.fetchData()
                  }, 1.5 * 1000)
                }).catch(() => {
                  this.$message({
                    type: 'info',
                    message: '已取消操作'
                  });
                });
    },
  }
}
