import axios from 'axios';
import permission from '@/directive/permission/index.js' // 权限判断指令
import gettoken from '../../../../src/store/modules/user.js'
export default {
  directives: { permission },
  data() {
    return {
      dialogVisible:false,
      initSuccess:false,
      timeline: [],
      tag:true,
      totalCount:1,
      detailList:{

        content:'',
        mesID:'',
        readID:'',
        addtime:'',


      },

    }
  },
  created(){

    this.getList()
  },
  methods:{
    getList(){
      var vm=this
      let mytoken=gettoken.state.token
      const path = 'http://127.0.0.1:5000/api/message';
            axios.get(path,{params:{token:mytoken}})
              .then((res) => {
                vm.timeline = res.data;
                // vm.totalCount=Object.keys(this.list).length;
                vm.initSuccess=true
                vm.$message({
                          message: '数据加载完成',
                          type: 'success'
                 });
              })
              .catch((error) => {

                vm.$message({
                        type: 'info',
                        message: '获取数据失败，请检查网络'+error
                });

              });


    },
    handleDelete(row) {
      var vm=this
        this.$confirm('您即将删除一条留言，确定吗', '提示', {
                  confirmButtonText: '确定删除',
                  cancelButtonText: '点错了',
                  type: 'warning'
                }).then(() => {
                  axios.post('http://127.0.0.1:5000/api/deletemessage', {
                     
                     mesID:row.mesID
                    })

                    .then(function (response) {
                    //  this.$refs[temp].resetFields();
                      vm.$message({
                          type: 'success',
                          message: '删除成功!'
                        });
                    })
                    .catch(function (error) {
                      vm.$message({
                          type: 'info',
                          message: error
                        });
                  })

                  setTimeout(() => {
                    this.getList()
                  }, 1.5 * 1000)
                }).catch(() => {
                  this.$message({
                    type: 'info',
                    message: '已取消删除'
                  });
                });
    },
    detail(item){
      this.detailList.addtime=item.addtime;
      this.detailList.content=item.content;
      this.detailList.mesID=item.mesID;
      this.detailList.readID=item.readID;


      this.dialogVisible=true
    },
    tableRowClassName({row, rowIndex}) {
            if (row.important==1) {
              return 'important_1';
            } else if (row.important==2) {
              return 'important_2';
            }else if (row.important==3) {
              return 'important_3';
            }
            return '';
     }
  }
}
