import { fetchList } from '@/api/article'
import axios from 'axios';
export default {

  data() {
    return {
      list: null,
      listQuery: {
        page: 1,
        limit: 5,
        type: this.type,
        sort: '+id'
      },
      loading: false,
      listLoading:true
    }
  },
  created() {
    this.getList()
  },
  methods: {
    getList() {
      var vm=this
      this.loading=false;
      const path = 'http://127.0.0.1:5000/api/books';
            axios.get(path)
              .then((res) => {
                vm.list = res.data;
                vm.loading=false;
                vm.listLoading=false
                vm.$message({
                        type: 'success',
                        message: '获取数据成功'
                      });
              })
              .catch((error) => {
                // eslint-disable-next-line
                console.error('出错啦！获取数据失败');
                vm.loading=false;
                vm.listLoading=false
              });

    }
  }
}
