import axios from 'axios';
export default {
  data() {
    return {
      dialogVisible:false,
      initSuccess:false,
      timeline: [{
          content: '活动按期开始',
          time: '2018-04-15'
        }, {
          content: '通过审核',
          time: '2018-04-13'
        }, {
          content: '创建成功',
          time: '2018-04-11'
        }],
      tag:true,
      totalCount:1,
      detailList:{
        title:'初始化',
        author:'',
        content_short:'',
        content:'',
        time:'',
        important:'',

      },

    }
  },
  created(){

    this.getList()
  },
  methods:{
    getList(){
      var vm=this
      const path = 'http://127.0.0.1:5000/api/announcement';
            axios.get(path)
              .then((res) => {
                vm.timeline = res.data;
                // vm.totalCount=Object.keys(this.list).length;
                vm.initSuccess=true
                vm.$message({
                          message: '数据加载完成',
                          type: 'success'
                 });
              })
              .catch((error) => {

                vm.$message({
                        type: 'info',
                        message: '获取数据失败，请检查网络'+error
                });

              });


    },

    detail(item){
      this.detailList.title=item.title;
      this.detailList.author=item.author;
      this.detailList.content_short=item.content_short;
      this.detailList.content=item.content;
      this.detailList.time=item.time;
      this.detailList.important=item.important;

      this.dialogVisible=true
    }
  }
}
